import  { EDIT_ITEM, ListState, SELECT_ITEM, ListItem, ListActionTypes} from "./types";

const initialState: ListState = {
    recipes: []
}

export function listReducer(state = initialState, action: ListActionTypes) : ListState {
    switch (action.type) {
        case "EDIT_ITEM": {
            return  state
        }
        case "SELECT_ITEM": {
            return  state
        }
        default:
            return state
    }
}
