import React from 'react';
import './Recipes.css'

interface IRecipe {
    id: number,
    name: string,
    price: number,
    img: string,
    recipyDesctiption: string
}

interface IRecipeProps {
    recipe: IRecipe
    onSelect: () => void
}

function Recipe(props: IRecipeProps) {
    return (
        <div className="listItem" onClick={props.onSelect}>
            <div> {props.recipe.name}</div>
            <img src={props.recipe.img}/>
            <div> {props.recipe.recipyDesctiption}</div>
        </div>
    )
}

class Recipes extends React.Component {

    state
     recipes: IRecipe[] = [{
         id: 1,
        name: "Borch",
        price: 10,
        img: "https://gotovim-doma.ru/images/recipe/0/0a/00ad9d3b1ff86a92ce5935bc47a1bdee_l.jpg",
        recipyDesctiption: "Borch description"
    }, {
         id: 2,
        name: "Fish",
        price: 20,
        img: "https://miro.medium.com/max/1899/1*mJ11PM4ZSBF0ZIAj5TebIw.png",
        recipyDesctiption: "Fish description"
    }];

     handleSelect(id: number) {
        console.log("aaa" + id)
     }

     renderRecipes() {
         return (
             <div className="list">
                 {
                     this.recipes.map(recipe => {
                        return <Recipe recipe={recipe} onSelect={ this.handleSelect.bind(this, recipe.id) } key={recipe.id}/>
                     })
                 }
                 </div>
         )
     }

    render(): React.ReactElement<any, string | React.JSXElementConstructor<any>> | string | number | {} | React.ReactNodeArray | React.ReactPortal | boolean | null | undefined {
        return (
            <div className="Recipes">
                {
                    this.renderRecipes()
                }
            </div>
        )
    }
}


export default Recipes;
