import React from 'react';
import '../App.css';

interface IRecipe {
    id: number,
    name: string,
    price: number,
    img: string,
    recipyDesctiption: string
}

function Recipe(recipe: IRecipe) {
    return (
        <div>
            <div> {recipe.name}</div>
            <img src={recipe.img}/>
            <div> {recipe.recipyDesctiption}</div>
        </div>
    )
}

class Recipes extends React.Component {

     recipes: IRecipe[] = [{
         id: 1,
        name: "Borch",
        price: 10,
        img: "https://gotovim-doma.ru/images/recipe/0/0a/00ad9d3b1ff86a92ce5935bc47a1bdee_l.jpg",
        recipyDesctiption: "Borch description"
    }, {
         id: 2,
        name: "Fish",
        price: 20,
        img: "https://miro.medium.com/max/1899/1*mJ11PM4ZSBF0ZIAj5TebIw.png",
        recipyDesctiption: "Fish description"
    }];

     renderRecipes() {
         return (
             <div className="list">
                 {
                     this.recipes.map(recipe => {
                         return  <Recipe
                             id={recipe.id}
                             name={recipe.name}
                             price={recipe.price}
                             img={recipe.img}
                             recipyDesctiption={recipe.recipyDesctiption}
                             key = {recipe.id}/>
                     })
                 }
                 </div>
         )
     }

    render(): React.ReactElement<any, string | React.JSXElementConstructor<any>> | string | number | {} | React.ReactNodeArray | React.ReactPortal | boolean | null | undefined {
        return (
            <div className="App">
                {
                    this.renderRecipes()
                }
            </div>
        )
    }
}


export default Recipes;
